import React from 'react';
import { connect } from 'react-redux';
import socket from '../socket';
import io from 'socket.io-client';

import { classToggler, userValidation, logError, renderIf } from './../../lib/util.js';
// import io from '../../../server.js';
// const socket = io.connect('http://10.114.9.26:3000');

class Chat extends React.Component {
  constructor(props){
    super(props);
    this.state = { username: '', message: '',  client: socket(), messages: []};

    this.socket = io('http://10.114.9.26:3000');

    this.socket.on('new_message', function(data){
        addMessage(data);
    });

    const addMessage = data => {
        console.log(data);
        this.setState({messages: [...this.state.messages, data]});
        console.log(this.state.messages);
    };
  }


  handleChange = e => {
    let { name, value } = e.target;
    this.setState({
      [name]: value,
    });
  };

  handleSendMessage = e => {
    // var socket = io.connect('http://10.114.9.26:3000');
    // socket.emit('new_message', {message : this.state.message });
    // this.setState(state => ({
    //     message: '',
    //   }));

      return this.state.client.message(this.state.message, (err, data) => {
        if (err)
        {
            console.log("err: ", err);
            return console.error(err)
        }

        console.log("data: ", data);
        this.state.messages.push(data);
        console.log("this.state.messages: ", this.state.messages);
        console.log("this.state.socket.newMessage: ", this.state.socket.newMessage);
        newMessage
      })
  };

  render() {
    // var socket = io.connect('http://10.114.9.26:3000');

    // //buttons and inputs
    // var message = $("#message");
    // var username = $("#username");
    // var send_message = $("#send_message");
    // var send_username = $("#send_username");
    // var chatroom = $("#chatroom");
    // var feedback = $("#feedback");

    // //Emit message
    // send_message.click(function(){
    //     socket.emit('new_message', {message : message.val()});
    // })

    // //Listen on new_message
    // socket.on("new_message", (data) => {
    //     feedback.html('');
    //     message.val('');
    //     chatroom.append("<p class='message'>" + data.username + ": " + data.message + "</p>");
    // })

    // //Emit a username
    // send_username.click(function(){
    //     socket.emit('change_username', {username : username.val()});
    // })

    // //Emit typing
    // message.bind("keypress", () => {
    //     socket.emit('typing');
    // })

    // //Listen on typing
    // socket.on('typing', (data) => {
    //     feedback.html("<p><i>" + data.username + " is typing a message..." + "</i></p>");
    // })
    return (
        <div>
            <header>
                <h1>Super Chat</h1>
            </header>
            <section>
                <div id="change_username">
                    <input id="username" type="text" name="username" onChange={this.handleChange} value={this.state.username}/>
                    <button id="send_username" type="button">Change username</button>
                </div>
            </section>
            <section id="chatroom">
                <section id="feedback"></section>
                {this.state.messages.map((msg, id) => {
                    return (
                        <div key={id}>
                            <p>{msg.username}: {msg.message}</p>
                        </div>
                    )
                })}
            </section>
            <section id="input_zone"> 
                <input id="message" className="vertical-align" type="text" name="message" onChange={this.handleChange} value={this.state.message} />
                <button id="send_message" className="vertical-align" type="button" onClick={this.handleSendMessage}>Send</button>
            </section>
        </div>
    );
  }
}

let mapStateToProps = state => ({
  userAuth: state.userAuth,
  userProfile: state.userProfile,
});

let mapDispatchToProps = dispatch => ({

});

export default connect(mapStateToProps, mapDispatchToProps)(Chat);
