import React from 'react';

class ProductForm extends React.Component {
  constructor(props){
    super(props)
    this.state = props.userProfile ? {...props.userProfile} :{
      productName: '',
      description: '',
      image: '',
      price: '',
      acceleration: '',
      year: '',
      power: '',
    };
  }
  handleSubmit = e => {
    e.preventDefault();
    return this.props.onComplete(this.state);
  };
  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    })
  };
  render() {
    return (
      <section className='product-form'>
        <form className='form' onSubmit={this.handleSubmit}>
          <h2 className='title'>product</h2>
          <label htmlFor='productName' className='profileFormLabel'>productName: </label>
          <input 
            type="text" 
            placeholder="productName"
            value={this.state.productName || ''}
            name="productName"
            onChange={this.handleChange}/>
          <label htmlFor='description' className='profileFormLabel'>description: </label>
          <input 
            type="text" 
            placeholder="description"
            value={this.state.description || ''}
            name="description"
            onChange={this.handleChange}/>
          <label htmlFor='image' className='profileFormLabel'>Product img URL: </label>
          <input 
            type="text" 
            placeholder="img url"
            value={this.state.image || ''}
            name="image"
            onChange={this.handleChange}/>
          <label htmlFor='price' className='profileFormLabel'>price: </label>
          <input 
            type="text" 
            placeholder="price"
            value={this.state.price || ''}
            name="price"
            onChange={this.handleChange}/>
          <label htmlFor='acceleration' className='profileFormLabel'>acceleration: </label>
          <input 
            type="text" 
            placeholder="acceleration"
            value={this.state.acceleration || ''}
            name="acceleration"
            onChange={this.handleChange}/>
          <label htmlFor='year' className='profileFormLabel'>year: </label>
          <input 
            type="text" 
            placeholder="year"
            value={this.state.year || ''}
            name="year"
            onChange={this.handleChange}/>
          <label htmlFor='power' className='profileFormLabel'>power: </label>
          <input 
            type="text" 
            placeholder="power"
            value={this.state.power || ''}
            name="power"
            onChange={this.handleChange}/>
          <div className=''>
            <button className='formButton' type="submit">Submit</button>
          </div>
        </form>  
      </section>
    );
  }    
}  

export default ProductForm;
